```c
#include<stdio.h>
void sort_(int all[], int begin,int end);//快速排序声明
int main()
{
	int all[100], n, x;
	scanf("%d", &n);
	int p = n, z = 1, t = 0;
	scanf("%d", &all[0]);
	for (x = 1; x < n; x++)
	{
		t = 0;
		int y, c;
		scanf("%d", &c);
		for (y = x - 1; y >= 0; y--)
		{
			if (c == all[y])
			{
				p--;//另外再定义一个变量计算数据
				t = 1;
				break;//直接退出y的for循环
			}
		}
		if (t == 1)
			continue;//如果成立就跳掉下面两行
		all[z] = c;/*其它数据都要更改，所以再定义一个变量来锁定数  
        组位置*/
		z++;
	}
	sort_(all,0,p-1);
	printf("%d\n", p);
	for (int x = 0; x <= p-1; x++)
		printf("%d ", all[x]);
	return 0;
}
void sort_(int all[], int begin, int end)//快速排序实现
{
	if (begin >= end)//如果指向同一个位置就返回
		return;
    int i = begin, j = end, k = all[begin];//排序工作初始化
	while (i < j)
	{
		while (i < j && all[j] > k)
			j--;
		if (i<j)
			all[i++] = all[j];
			while (i < j && all[i] < k)
				i++;
			if (i<j)
			all[j--] = all[i];
	}
	all[i] = k;
	sort_(all, begin, i - 1);//分别对前后进行排序递归
	sort_(all, i + 1, end);
}
```
