{
	int m,n;
	cin>>m>>n;
	for(int i=1;i<=n;i++)
	cin>>w[i]>>c[i];
	for(int i=1;i<=n;i++){
		for(int j=1;j<=m;j++){
			if(j<w[i]) f[i][j]=f[i-1][j];
			else f[i][j]=max(f[i-1][j],f[i-1][j-w[i]]+c[i]);//动态转移方程
		}
	}
	cout<<f[n][m];
	return 0;
}//临时手打，可能不会过
```


