    for(i = 1;i <= n;i++)
    {
        cin >> w >> v;
        for(j = W;j >= w;j--)
        {
            if(a[j - w] == 1)
            {
                t = s[j];
                s[j] = w * v + s[j - w];
                if(t > s[j]) s[j] = t;
                a[j] = 1;
            }
        }
    }
    int max = 0;
    for(i = W;i >= 0;i--)
        if(a[i] == 1)
            if(s[i] >= max) max = s[i]; 
    cout << max;
    return 0;
}

