      vsum:=vsum+v[j];psum:=psum+p[j];max1:=max1+v[j]*p[j];//如果是一，说明是选着的物品，那么就累加
    end;
    if vsum<=m then if max<max1 then max:=max1;//判断
  end;
  writeln(max);//输出
