#pragma GCC optimize(3)//作死开O3，考试别这么用（逃
//加O3因为本以为会TLE结果并不
#define ll long long
ll qpow(int a,int p){//快速幂
    ll ans=1;
    while(p){
        if(p%2)ans*=a;
        p>>=1;
        a*=a;
    }
    return ans;
}
int main(){
