        int j;
        for(j=0;b[j];j++)b[j]=0;
        b[j]=1;
    }
    long long ans=0;
    for(register int i=0;i<n;i++)ans+=b[i]*qpow(k,i);
    //算答案，这里也可以打个k次方的表 避免重复计算= =
    printf("%lld",ans);
    return 0;
}
