        for(int i=0; i<n; i++) //左端点
        {
            int j=(l+i)%n;//右端点，因为是圆，所以有右端点小于左端点的情况
            for(int k=i; k!=j; k=(k+1)%n) //分割合并，这里不能写成k<j，因为是圆
                f[i][j]=max(f[i][j],f[i][k]+f[(k+1)%n][j]+num[i]*num[(k+1)%n]*num[(j+1)%n]);//状态转移方程
