            }
        }
    }
    int ans=-1;
    for(int i=1;i+l-1<=n;i++)//上面讲的
    {
        ans=max(ans,dp[i][i+l-1]);
        //要去最大值
    }
    printf("%d\n",ans);//输出答案
    cpp_is_better_than_any_language;
    //cpp is better than any language!
}
