    fo(i,1,n) fo(j,1,m) scanf("%d",&tim[i][j]);
    fo(i,1,n*m)
    {
        calc[num[i]]++;
        int p1=num[i],p2=calc[num[i]],w=whe[p1][p2],t=tim[p1][p2],res=0;
        fo(j,last[p1]+1,10000)
        {
            if(vis[w][j]) res=0;
            else res++;
            if(res==t) break;
        }
        int tmp=j;
        fo(j,tmp-t+1,tmp) vis[w][j]=1;
        last[p1]=tmp;
        ans=max(ans,last[p1]);
    }        
    printf("%d\n",ans);
    return 0;
