#include <bits/stdc++.h>
using namespace std;
int n,m,seq[30*30],num[30][30],t[30][30],cnt[30],last[30];
bool used[30][8000];
bool check(int start,int len,int mac)
{
    bool f=true;
    for(int i=start;i<start+len;i++) if(used[mac][i]) f=false;
    return f;
}
int main()
{
    cin >> m >> n;
    for(int i=1;i<=n*m;i++) cin >> seq[i];
    for(int i=1;i<=n;i++)
        for(int j=1;j<=m;j++)
            cin >> num[i][j];
    for(int i=1;i<=n;i++)
        for(int j=1;j<=m;j++)
            cin >> t[i][j];
    for(int i=1;i<=n*m;i++)
    {
        cnt[seq[i]]++;
        int x=seq[i],y=cnt[seq[i]],z=num[x][y];
        for(int j=last[x];;j++)
        {
            if(check(j,t[x][y],z))
            {
                for(int k=j;k<j+t[x][y];k++) used[z][k]=true;
                last[x]=j+t[x][y];
                break;
            }
        }
    }
    int res=0;
    for(int i=1;i<=n;i++)
    {
        res=max(res,last[i]);
    }
    cout << res;
    return 0;
}
