struct mark{
       int a,h;
};
mark mian[5000];
bool cmp(mark x,mark y)
{
    if(x.a>y.a) return 1;
	if(x.a==y.a&&x.h<y.h) return 1;
    return 0;  
}
int main()
{
    int n,m,pass,s=0;
    cin>>n>>m; 
    //输入考试人数与预计通过人数

    for(int i=0;i<n;i++)
    {
        cin>>mian[i].h>>mian[i].a;
        //循环，输入考试者的成绩与号数
    }

    sort(mian,mian+n,cmp);     
    //根据分数与号数排序
	pass=mian[m*3/2-1].a;      //计算分数线
    for(int i=0;i<n;i++)
    {
    	if(stu[i].a>=pass) s++;//计算通过人数
	}

	cout<<pass<<" "<<s<<endl;
	for(int i=0;i<s;i++)        //输出通过总人数 
        cout<<mian[i].h<<"   "<<mian[i].a<<endl;
    return 0;                   //好习惯
}
```

