#include<stdio.h>
int a[10001],n,sum,m,num[10001],b[10001],fen,s;
void quicksort(int a[],int l,int r)//快排程序
{
    int i,j,mid,p,p1;
    i=l;
    j=r;
    mid=a[(l+r)/2];
    do
    {
        while(a[i]<mid) i++;
        while(a[j]>mid) j--;
        if(i<=j)
        {
            p=a[i];
            p1=num[i];
            a[i]=a[j];
            num[i]=num[j];
            a[j]=p;
            num[j]=p1;
            i++;
            j--;
        } 
    }while(i<=j);    
    if(i<r) quicksort(a,i,r); 
    if(l<j) quicksort(a,l,j);
}
int main()//开始主程序
{
    int i,j,p2;
    scanf("%d%d",&n,&m);
    for(i=1;i<=n;i++)
    scanf("%d%d",&num[i],&a[i]);//录入数据
