    tmp=p[m*3/2-1].s;                                //求出晋级面试最低所需分数
    for(i=0;i<n;i++)
        if(p[i].s>=tmp) ans++;                      //如果大于晋级面试最低所需分数则晋级人数加一
    printf("%d %d\n",tmp,ans);
