int a[10001],n,sum,m,num[10001],b[10001],fen,s;
void quicksort(int head,int tail)//不要依赖STL
{
    int i,j,mid,p,p1;
    i=head;
    j=tail;
    mid=a[(head+tail)/2];
    do
    {
        while(a[i]<mid) i++;
        while(a[j]>mid) j--;
        if(i<=j)
        {
            p=a[i];
            p1=num[i];//号码要跟分数一起遍
            a[i]=a[j];
            num[i]=num[j];
            a[j]=p;
            num[j]=p1;
            i++;
            j--;
        } 
    }while(i<=j);    
    if(i<tail) quicksort(i,tail); 
    if(head<j) quicksort(head,j);
}
