int isInori(char a);
bool isInmat(char a);
int isInori(char a){
    for(int i=0;i<size;i++){
        if(oric[i]==a) return i;  //返回所在的下标
    }
    return -1;
}
bool isInmat(char a){
    for(int i=0;i<size;i++){
        if(matchc[i]==a) return true;
    }
    return false;
}
int main(){
