            if((int)ori[i]>64 && (int)ori[i]<91 && (int)match[i]>64 && (int)match[i]<91){  //确保一定是大写字母
               if(isInmat(match[i])) continue;
                else {
                    matchc[size] = match[i];
                    if(isInori(ori[i])!=-1) tag = 1;  //一对多
                    oric[size] = ori[i];
                    size++;
