```cpp
#include<cstdio>
int n,A0,A1,B0,B1;
int GCD(int t1,int t2)
{
    return t2==0?t1:GCD(t2,t1%t2);
}
bool Judge(int t)
{
    if (t%A1)
      return 0;
    return GCD(t/A1,A0/A1)==1&&GCD(B1/B0,B1/t)==1; //皆为同理。
}
