#include <bits/stdc++.h>
using namespace std;
int a0,a1,b0,b1,i,k;
int test_number,ans;
int gcd(int a,int b){
	if (b==0) return a;
	return gcd(b,a%b);
}
int lcm(int a,int b){
	return a/gcd(a,b)*b; 
}
void calc_answer(){
	int maxn=sqrt(b1);ans=0;
	for(int i=1;i<=maxn;i++)
	if (b1%i==0){
		if (gcd(i,a0)==a1&&lcm(i,b0)==b1) ans++;
		int k=b1/i;if (k==i) break;
