```cpp
#include<iostream>
#include<cstdio>
#include<cmath>
using namespace std;
inline bool pd(int);
int n;
int main() {
	scanf("%d", &n);
	for(int i=2;i<=sqrt(n);i++)
		if (n%i == 0 && pd(n/i)) {
			printf("%d", n/i);
			return 0;
		}
