#include <iostream>

using namespace std;

int main(void)
{
	int n;        		//输入的正整数
	int small;   		//较小的质因数
	int big;      		//较大的质因数

	cin >> n;

	//寻找n的质因数
	for(small = 2; small * small <= n; small++)
	{
		int small_is_prime = 1;		//1-质数，0-合数
		int big_is_prime = 1;		//1-质数，0-合数

		//small不是n的因数，跳过本次循环
		if(n % small != 0)
		{
			continue;
		}

       	 big = n / small;

		for(int i = 2; i * i <= small; i++)
		{
			//small是合数
			if(small % i == 0)
			{
				small_is_prime = 0;
				break;
			}
		}
		for(int i = 2; i * i <= big; i++)
		{
			//big是合数
			if(big % i == 0)
			{
				big_is_prime = 0;
				break;
			}
		}

		if(small_is_prime == 1 && big_is_prime == 1)
		{
        	cout << big << endl;
			break;
		}
	}


	return 0;
}
