#define Close fclose(stdin); fclose(stdout);
using namespace std;
int a[10010][110],s[10010][110],nx[10010][110];
int lt[10010][200];
lolo n,m,ans,st;

inline int read()
{
	int k=1,sum=0;
	char c=getchar();
	for(; '0'>c || c>'9';  c=getchar())
		if(c=='-') k=-1;
	for(; '0'<=c && c<='9';c=getchar())
		sum=sum*10+c-'0';
	return sum*k;
}

inline void write(int x)
{
    if(x>9) write(x/10);
    putchar(x%10+'0');
}

void in_1()
{
    n=read(),m=read();
    for (int i=1; i<=n; i+=1)
    { 
        int gs=0;
        for (int j=0; j<m; j+=1)
        {
            a[i][j]=read(),s[i][j]=read(),nx[i][j]=gs;//a是用来存判断的，s是告示牌上的数字，nx是用来预处理该房间属于该层第几有门房间           
            if (a[i][j]==1) lt[i][gs]=j,gs+=1;
        }
        for (int j=m-1; j>=1; j-=1)
          if (nx[i][j]==gs) nx[i][j]=0;
          else                break;
        lt[i][110]=gs;//因为要用取模来模拟环形所以用110来存该层有门房间数
    }
    st=read();  
}

void out_1()
{
    printf("%lld\n",ans%20123);
}

void work()
{
	int now=st;
	for (int i=1; i<=n; i+=1)
	{
		ans+=s[i][now]%Mod;
		now=lt[i][(nx[i][now]+s[i][now]-1)%lt[i][110]];//这是核心计算过程哦~~
	}
