    dp[0][0]=1;
    for(int i=1;i<=n;i++)
    for(int j=0;j<=m;j++)
    for(int k=0;k<=a[i];k++)
    dp[i][j]=(dp[i-1][j-k]+dp[i][j])%1000007;
    cout<<dp[n][m]<<endl;
    return 0;
}
