const int N = 1005;
char s1[N], s2[N];
int l1, l2;
int main() {
    scanf("%s%s", s1+1, s2+1);
    l1 = strlen(s1+1);
    l2 = strlen(s2+1);
    for(int i = 1, j = 1; i <= l2; i++, j++) {
        if(j > l1) j = 1; //如果长度超出，则从1开始（和mod实际上差不多）
        int g = 0;
        if('A'<=s1[j]&&s1[j]<='Z')
            s1[j] = s1[j]-'A'+'a';
        if('A'<=s2[i]&&s2[i]<='Z') { 
            g = 'A'-'a';
            s2[i] = s2[i]-'A'+'a';
        }
        char c = s2[i] - (s1[j]-'a');
        if(!('a'<=c&&c<='z')) c += 26; //补回来
        putchar(c+g);
    }
    return 0;
}
