```cpp
#include<cstdio>
#include<iostream>
#include<cstring>
#include<algorithm>
#include<cmath>
using namespace std;
int mapx[27][27];
char K[1001],C[1001];//用K和C来存输入，K为密钥，C为密文，类型是字符类型
int m[1001],k[1001],c[1001],Bigc[1001];//Bigc用来判断密文中的大写字母，m，k，用于将K，C输入的字母转为整数类型的字母序。
//打表，用mapx[x][y]来打表记录XY对应的明文。原理在上面讲过。
void PRINTS()
{
    for(int i=1;i<=26;i++)
        for(int j=1;j<=26;j++)
        {
            if(j-i<0)
                mapx[i][j]=j-i+26+1;
            else mapx[i][j]=j-i+1;
        }
    return;
}
int main()
{
    cin>>K;
    memset(Bigc,0,sizeof(Bigc));
    int len=strlen(K);//记录密钥的长度
    for(int i=0;i<len;i++)
    {
        if(K[i]<97)//大写字母的ASCII码为65—90，小写字母为97—122。当小于97时为大写字母，减64即为字母序。
        {
            k[i]=K[i]-64;
        }
        else
            k[i]=K[i]-96;//同理，小写字母减96即为字母序。
    }
    cin>>C;
    int len1=strlen(C);
    for(int i=0;i<len1;i++)
    {
        if(C[i]<97)
        {
            c[i]=C[i]-64;
            Bigc[i]=1;//记录是否为大写字母。
        }
        else 
            c[i]=C[i]-96;        
    }
    if(len1>len)//将K的大小扩大到M的大小
    {
        for(int i=0;i<len1;i++)
        {
            if(i>=len)
                k[i]=k[i-len];
            else 
            k[i]=k[i];
        }
    }
    PRINTS();//打表
    for(int i=0;i<len1;i++)
    {
        m[i]=mapx[k[i]][c[i]];//用m来存明文的字母序
    }
    for(int i=0;i<len1;i++)
    {
        if(Bigc[i]>0)
        {
            m[i]+=64;//如果对应位置的密文为大写字母，那么明文也变为大写字母
        }
        else
            m[i]+=96;//变成小写字母
        cout<<(char)m[i];
    }    
}
```
