        cin >> m.left >> m.right;
        queue.push_back(m);
    }
    sort(queue.begin(), queue.end(), cmp);

    bign reward, maxReward = 0;
    bign product = kingLeft;
    for (int i = 0; i < n; i++)
    {
        reward = product / queue[i].right;
        if (reward >= maxReward)
            maxReward = reward;
        product *= queue[i].left;
    }

    cout << maxReward << endl;
    return 0;
}
