inline int read()
{
	int x = 0, w = 1;
	char c = getchar();
	while(c < '0' || c > '9') { if (c == '-') w = -1; c = getchar(); }
	while(c >= '0' && c <= '9') { x = x * 10 + c - '0'; c = getchar(); }
	return x * w;
}

inline bool cmp(node a, node b) { return 1ll * a.l * a.r < 1ll * b.l * b.r; }

inline int gett(int x)
{
	if(!x) return 1; 
	int res = 0; 
	while(x)
	{
		res++; 
		x /= 10; 
	}
	return res; 
}guowangyouxi

inline void times(int x)
{
	bool flag = 0; 
	for(int i = cnt; i >= 0; i--)
	{
		sum[i] *= x;
		int up = sum[i] / 10000;
		sum[i] %= 10000;
		if(up > 0)
		{
			if(i == cnt) { sum[cnt + 1] += up; flag = 1; }
			else sum[i + 1] += up; 
		}
	}
	if(flag) cnt++;
}

inline void divite(int x)
{
	int rem = 0; rnt = cnt; 
	for(int i = cnt; i >= 0; i--)
	{
		num[i] = (rem * 10000 + sum[i]) / x; 
		rem = (rem * 10000 + sum[i]) % x;
	}
	while(!num[rnt]) rnt--;
}

inline bool compare()
{
	if(rnt > tot) return 1;
	if(rnt < tot) return 0;
	for(int i = rnt; i >= 0; i--)
	{
		if(num[i] > ans[i]) return 1;
		if(num[i] == ans[i]) continue;
		if(num[i] < ans[i]) return 0; 
	}
}

inline void cp()
{
	tot = rnt; 
	for(int i = 0; i <= tot; i++) ans[i] = num[i]; 
}

