        len = 1;
    }
    bign (int num) { *this = num; }
    bign (const char *num) { *this = num; }

    bign operator = (const int num){
        char s[MAXN];
        sprintf(s, "%d", num);
        *this = s;
        return *this;
    }

    void clean(){
        while(len > 1 && !s[len-1]) len--;
    } 

