        for(int i = 0; i < len; i++) {
        	if (isdigit(num[len-i-1])) s[i] = num[len-i-1] - '0';
        	else s[i] = num[len-i-1] - 'A' + 10;
        }
        clean();
        return *this;
    }

    bign operator + (const bign &b) const{
        bign c;
        c.len = max(len, b.len);
        for (int i = 0; i < c.len; i++){
            c.s[i] += s[i] + b.s[i];
            c.s[i+1] += c.s[i] / B; // B default = 10
            c.s[i] %= B;
        }
        if (c.s[c.len]) c.len++;
        c.clean();
        return c;
    }

    bign operator += (const bign &b){
        *this = *this + b;
        return *this;
    }

    bign operator * (const bign &b) {//*
        bign c;
        c.len = len + b.len;
        for(int i = 0; i < len; i++)
            for(int j = 0; j < b.len; j++){
            	c.s[i+j] += s[i] * b.s[j];
            }

        for(int i = 0; i < c.len; i++){
        	c.s[i+1] += c.s[i]/10;
