struct peo
{
	int a,b;
}p[1100];
bool cmp(peo x,peo y)
{
	return (x.a*x.b)<(y.a*y.b);//按照 两个值的乘积进行排序
}
int m[100000],l=1;
void mutiply(int x)//高精乘法
{
	int add=0;
	for(int i=1;i<=l+10;i++)
	{
		int tmp=m[i]*x+add;
		m[i]=tmp%10;
		add=tmp/10;
	}
	l+=10;
	while(m[l]==0) l--;
}
void get(int x)
{
	memset(res,0,sizeof(res));
	int tmp,l1=0;
	for(int i=l;i>=1;i--)
	{
		tmp=tmp*10+m[i],l1++;
		if(tmp<x) continue;
		res[l1]=tmp/x;
		tmp%=x;
	}//高精除法，注意我存的时候正过来存的，所以还要倒过来一下
	for(int i=1;i<=(l1+1)/2;i++)
		swap(res[i],res[l1-i+1]);
	while(res[l1]==0 && l1>1) l1--;
    //比较大小
	if(l1>la)
	{
		la=l1;
		for(int i=la;i>=1;i--)
			ans[i]=res[i];
	}
	else if(l1==la)
	{
		int f=0;
		for(int i=la;i>=1;i--)
		{
			if(ans[i]<res[i])
			{
				f=1;
				break;
			}
			if(ans[i]>res[i]) break;
		}
		if(f)
