#define LL long long
using namespace std;
int Euler(int n) {
    int res=n;
    for (int i=2;i*i<=n;i++) {
        if (n%i==0) {
            res=res/i*(i-1);
        }
        while (n%i==0) n/=i;
    }
    if (n>1) res=res/n*(n-1);
    return res;
}
LL qpow(LL a,LL b,LL mod) {
    LL res=1;
    while (b) {
        if (b&1) res=res*a%mod;
        a=a*a%mod;
        b>>=1;
    }
    return res%mod;
}
