{
	if(b==0)
	{
		x=1;y=0;
		return ;
	}
	exgcd(b,a%b);
	xx=x;x=y;y=xx-(a/b)*y;
	return ;
}
int main()
{
	ios::sync_with_stdio(false);
	cin>>a>>b;
	exgcd(a,b);
	cout<<(x+b)%b;
	return 0;
}
