void exgcd(int a,int b)
{
    if (b==0)
      {
        x=1;y=0;
        return;
      }
    exgcd(b,a%b);
    k=x;x=y;
    y=k-a/b*y;
    return;
}
int main()
{
    scanf("%d%d",&a,&b);
    exgcd(a,b);
    cout<<(x+b)%b;
}
