void build(int deep,int num){
    //printf("%d\n",deep);
    int temp,i,u;
    if(deep==0){//第一层
        if(getstr[num-1]=='0'){
            scantr[deep][num]='B';
        }
        else{
            scantr[deep][num]='I';
        }
    }
    else{
        bool a=0,b=0,c=0;
