            for(u=temp-1;u<=temp;u++){
                if(scantr[i][u]=='F'){
                    scantr[deep][num]='F';
                    c=true;
                    break;
                }
                else if(scantr[i][u]=='B'){
                    scantr[deep][num]='B';
                    b=true;
                }
                else if(scantr[i][u]=='I'){
                    scantr[deep][num]='I';
                    a=true;
                }
                if(a&&b){
                    scantr[deep][num]='F';
                    c=true;
                    break;
                }
            }
            if(c)
                break;
        }
    }//确认字符串类型
    node[++top].data=scantr[deep][num];
