inline int read() {
	char c = getchar();
	int x = 0, f = 1;
	while(c < '0' || c > '9') {
		if(c == '-') f = -1;
		c = getchar();
	}
	while(c >= '0' && c <= '9') x = x * 10 + c - '0', c = getchar();
	return x * f;
}
int tree[100010];
char s[10001];
int len;
int build(int l,int r,int num) {
	if(num==2*len) {
		return 0;
	}
	if(l==r) {
		if(s[l]=='0') {
			tree[num]='B';
			return 'B';
		} else {
			tree[num]='I';
			return 'I';
		}
	}
	char ll=build(l,(l+r)/2,2*num),rr=build((l+r)/2+1,r,2*num+1);
	if(ll=='F'||rr=='F') {
		tree[num]='F';
		return 'F';
	} else {
		if(ll != rr) {
			tree[num]='F';
			return 'F';
		} else {
			if(ll == 'B')	{
				tree[num] = 'B';
				return 'B';
			} else {
				tree[num] = 'I';
				return 'I';
			}
		}
	}
}
void print(int x) {
	if(tree[2*x]) print(2*x);
	if(tree[2*x+1]) print(2*x+1);
	cout<<char(tree[x]);
}
int main() {
