```cpp
#include <cstdio>
#include <cstring>
using namespace std;
int const Maxn=10000;
int n,a1[Maxn<<1],l;
char a[Maxn<<1];
void work(int l,int r){
    if(l<r){//分状态
        work(l,l+(r-l)/2);
        work(1+l+(r-l)/2,r);
    }
    int tmp,tmp1;//计数
    tmp=tmp1=0;
    for(int i=l;i<=r;i++)
        if(a1[i])tmp1++;
        else tmp++;
    if(!tmp) printf("I");
    else if(!tmp1) printf("B");
    else printf("F");
}
