#include<iostream>
#include<string>
#include<cstdio>    //putchar()
#include<cmath>   //pow()
using namespace std;
// F 0     B -1     I 1
int n;
string str;
int divide(string s, int len){
    if(len==1){
        if(s[0]=='1'){
            putchar('I');
            return 1;
        }
        else{
            putchar('B');
            return -1;
        }
    }
    int a = divide(s.substr(0,len/2), len/2);
    int b = divide(s.substr(len/2, len), len/2);
    if(a==b){
        switch(a){
            case 0:{
                putchar('F');
                return 0;
                break;
            }
            case -1:{
                putchar('B');
                return -1;
                break;
            }
            case 1:{
                putchar('I');
                return 1;
                break;
            }
        }
    }else{
        putchar('F');
        return 0;
    }
}
int main(){
    cin>>n;
    cin>>str;
    n=pow(2, n);
    divide(str, n);
    return 0;
}
