using namespace std;
const int MAX=5000;
struct Node{
    char sign;
    int left,right;
    Node():left(0),right(0){};
}tree[MAX];

char judge(string list)
{
    if(list.find("0") == list.npos)
        return 'I';
    if(list.find("1") == list.npos)
        return 'B';
    return 'F';
}

void Build(int i,string list)
{
    tree[i].sign=judge(list);
    if(list.size() == 1) return ;
    tree[i].left=i<<1;
    tree[i].right=(i<<1)+1;
    int mid=list.size()/2;
    string ltemp,rtemp;
    ltemp=list.substr(0,list.size()/2); 
    rtemp=list.substr(list.size()/2,list.size()+1);
    Build(i<<1,ltemp);
    Build((i<<1)+1,rtemp);
}

void DFS(int i)
{
    if(tree[i].left)
        DFS(tree[i].left);
    if(tree[i].right)
        DFS(tree[i].right);
    cout<<tree[i].sign;
}

int main()
{
    string list;
    int N;
    cin>>N>>list;
    Build(1,list);
    DFS(1);
    return 0;
}


