void bl(string x){//对字符串做处理
	int len=x.size();//取长度
	if(len==1){//如果已经无法再分，就直接输出其类型
		putchar(check(x));
		return;
	}
	int mid=len/2;//取中间点，二分递归调用
	bl(x.substr(0,mid));//先输出左右两半的类型（substr函数是c++库函数，可以取string类型字符串的子串）
	bl(x.substr(mid));
	putchar(check(x));//最后输出自身类型
}
