var
  sum:array[0..70000]of char;//模拟线段树
  ch:char;
  n,x,x1:longint;
procedure pushup(rt:longint);
begin
  if (sum[rt*2]='F')or(sum[rt*2+1]='F')then//建造FBI树
    sum[rt]:='F' else
    if ((sum[rt*2]='B')and(sum[rt*2+1]='I'))or((sum[rt*2]='I')and(sum[rt*2+1]='B'))then
      sum[rt]:='F' else
      if sum[rt*2]=sum[rt*2+1] then
        sum[rt]:=sum[rt*2];
end;
function two(n:longint):longint;
var
  i:longint;
  s:longint;
begin
  s:=1;
  for i:=1 to n do
    s:=s*2;
  exit(s);
end;
procedure build(l,r,rt:longint);//建树操作
var
  m:longint;
begin
  if l=r then
  begin
    read(ch);
    if ch='1' then
      sum[rt]:='I' else
      sum[rt]:='B';
    exit;
  end;
  m:=(l+r)div 2;
  build(l,m,rt*2);
  build(m+1,r,rt*2+1);
  pushup(rt);
end;
procedure find(rt:longint);//遍历操作
begin
  if rt*2>x1 then
  begin
    write(sum[rt]);
    exit;
  end;
  if rt*2<=x1 then
    find(rt*2);
  if rt*2+1<=x1 then
    find(rt*2+1);
  write(sum[rt]);
end;
begin
  readln(n);
  x:=two(n); x1:=two(n+1)-1;
  build(1,x,1);
  find(1);
end.

