struct leaf{
    int type;  //0:B;1:I;2:F;
}le[2000];//可以表示在树上的位置，1为根,2、3往后.... 
int n,a[2000],lo=1,lon,l,r,o=0;
int ptf[2000];
inline int mi(int a,int b)
{
    if(b==0) return 1;
    int c=a;
    for(int i=2;i<=b;i++)
    c*=a;
    return c;
}
inline int get_l(int k);
inline int get_r(int k){
    if(k==1) return lo;
    if(k%2==0) return (get_r(k>>1)+get_l(k>>1))/2;
    return get_r(k>>1);
}
inline int get_l(int k){
    if(k==1) return 1;
    if(k%2==0) return get_l(k>>1);
    return (get_r(k>>1)+get_l(k>>1))/2+1;
}
void bulid_tree(int k){
    if(k==lon) return;
    l=get_l(k);
    r=get_r(k);

    int tot=0;
    for(int i=l;i<=r;i++)
        tot+=a[i];	
    le[k].type=2;
    if(tot==r-l+1) le[k].type=1;
    if(tot==0) le[k].type=0;


    bulid_tree(++k);
}
void print_tree(int k){
    ptf[++o]=le[k].type;
    if(k*2+1<lon) print_tree(k*2+1);
    if(k*2<lon) print_tree(k*2);
}
int main(){
    cin>>n;
    lo=mi(2,n);
    for(int i=1;i<=lo;i++)
        scanf("%1d",&a[i]);
    lon=lo*2;
    bulid_tree(1);
    print_tree(1);
    for(int i=o;i>=1;i--)
    {
        if(ptf[i]==1) printf("I");
        if(ptf[i]==0) printf("B");
        if(ptf[i]==2) printf("F");
    }
    return 0;
} 
