```cpp
#include <bits/stdc++.h> //万能头文件
using namespace std; //声明姓名空间

int n, m, x[100000], i, j, u, v;

void next() { //相当于next_permutation
	for (i = n - 1; i >= 1; i--) {
		for (j = n; j >= i + 1; j--) {
			if (x[i] < x[j]) { //排序
				m--; //缩小下次的范围，使程序AC！
				swap(x[i], x[j]);
				if (i + 1 != n) { //核心部分
					for (u = i + 1; u <= n - 1; u++) {
						for (v = u + 1; v <= n; v++) {
							if (x[u] > x[v]) {
								swap(x[u], x[v]);
							}
						}
					}
				}
				return ;
			}
		}
	}
}

