  b:array[1..10000] of boolean;
  n,m,i,x:longint;
procedure wm(j:longint);
  var i,k:longint;
  begin
    if j=n+1 then begin //当j=n+1时，说明列举好了一个
                    inc(x);
                    if x=m then
                      begin
                        for i:=1 to n-1 do//输出
