    if x=-1 then k:=a[j] //当x还等于-1的时候，可以直接从a[j]开始，节省时间
            else k:=1;
    for i:=k to n do
      if b[i] then// 这里就是普通的全排列，不作说明
        begin
          f[j]:=i;
          b[i]:=false;
          wm(j+1);
          b[i]:=true;
        end;
  end;
begin
  readln(n,m);
  for i:=1 to n do
    begin
      read(a[i]);
      b[i]:=true;
    end;
  x:=-1;//当x=m时，就已经列举到了+m后的数
  wm(1);
end.
