        }else{//意思为“钱够”
            money -= month2;//花掉的钱
            if(money >= 100){//如果没有100块就没办法给妈100元钱
                mum2 = money / 100 * 100;//除100找出有多少个100可以给妈妈，乘100就能恢复成整百的形式
                mum += mum2;//妈妈手上的钱要增加
                money -= mum2;//把给妈妈的钱去掉
            }
        }
    }
    if(month != 0) cout << -month << endl;//如果month不是0，则代表有某个月的钱不够花
    else {
        money += (mum / 100 * 20) + mum;//妈妈手上的钱分成一百份取其中的20份则是20%，手上的钱加上20%还给津津
        cout << money << endl;//输出
    }
}
