	for(int i=1;i<=n;i++)
		l[i]=read();
	for(int k=1;k<=n;k++)
	{
		//枚举断点 ti  
		memset(f,0,sizeof(f));
		s1=0,s2=0; 
		//一定要重置 

		//模板就不多说了，不会的同学直接背下来就行了。（当年我就这么干的） 
		for(int i=1;i<k;i++)
		{
			f[i]=1;
