				}
			}
			b[i] = Rmax+1;
			if(b[i]!=1) v[i] = true;
		}
		//左序列
		wei=p;
		for(int i=p-1; i>=0; i--) {
			int Lmax = 0;
			for(int j=i+1; j<=p; j++) {
				if(v[j] && a[i]<a[j] && Lmax<b[j]) {
					Lmax = b[j];
					wei = j;
				}
			}
			b[i] = Lmax+1;
			if(b[i]!=1) v[i] = true;
		}
//		for(int i=0;i<n;i++) {
//			System.out.printf("%d ",b[i]);
//		}
//		System.out.println();
		//找出左右最长的序列求和
		int Lmax=0,Rmax=0;
		for(int i=0;i<=p;i++) {
			Rmax = Math.max(Rmax, b[i]);
		}
		for(int i=n-1;i>=p;i--) {
			Lmax = Math.max(Lmax, b[i]);
		}
        //会多加一个p点，所以需要减一
		int allmax = Lmax+Rmax-1;
