        int max=0,w;
        for(w=0;w<i;w++){
            if(a[w]<a[i]&&dpl[w]>max)max=dpl[w];
        }
        dpl[i]=max+1;
    }
    dpr[N-1]=1;
    for(i=N-2;i>=0;i--){
        int max=0,w;
        for(w=i+1;w<N;w++){
            if(a[i]>a[w]&&dpr[w]>max)max=dpr[w];
        }
        dpr[i]=max+1;
    }
    int max=-1,sum;
    for(i=0;i<N;i++){
        sum=dpl[i]+dpr[i];
        if(sum>max)max=sum;
    }
    max-=1;
    printf("%d",N-max);
    return 0;
}
