#include <iostream>
using namespace std;
int a[150],b[150],c[150];
int main(){
    int n,i,j;
    cin>>n;
    for(i=1;i<=n;i++){
        cin>>a[i];
        b[i]=1;
        c[i]=1;
    }
    for(i=2;i<=n;i++){
        for(j=1;j<i;j++){
            if(a[i]>a[j] && b[j]+1>=b[i]){
                b[i]=b[j]+1;
            }
        }
    }
    for(i=n-1;i>=1;i--){     //边界问题
        for(j=i+1;j<=n;j++){    //注意！！！
            if(a[i]>a[j] && c[j]+1>=c[i]){
                c[i]=c[j]+1;
            }
        }
    }
    int m=-1;
    for(i=1;i<=n;i++){
        if(b[i]+c[i]>m){
            m=b[i]+c[i];
        }
    }
    m-=1;
    cout<<n-m;
    return 0;
}
```

