    for(int i=1;i<=n;i++)
    {
        c[i]=1;
        for(int j=1;j<=i-1;j++)
            if(a[j]<a[i]&&c[j]+1>c[i])  //从左往右求上升序列
            {
                c[i]=c[j]+1;
            }
    }
    for(int i=n;i>=1;i--)
    {
        b[i]=1;
        for(int j=i+1;j<=n;j++)
            if(a[j]<a[i]&&b[j]+1>b[i])     //从右往左求下降序列
            {
                b[i]=b[j]+1;
            }
    }
    for(int i=1;i<=n;i++)
    {
            if(c[i]+b[i]>maxa)   //把同一个节点的左右两边结果求出来，得到的结果就是最大的啦
            maxa=c[i]+b[i];
    }
    cout<<n-maxa+1;   //中间的那个人被算了两次，把他减掉
    return 0;
